mapper.select:
select *
FROM jsh_account_item
where 1=1


    and name like #{bindName}


    and type=#{type}



    and remark like #{bindRemark}

and ifnull(delete_flag,'0') !='1'

    limit #{offset},#{rows}

SELECT
COUNT(id)
FROM jsh_account_item
WHERE 1=1


    and name like #{bindName}


    and type=#{type}



    and remark like #{bindRemark}

and ifnull(delete_flag,'0') !='1'

mapper.update:
