mapper.select:
select tb.*, (select r.id from jsh_user_business ub left join jsh_role r on ub.value=concat("[",r.id,"]") and ifnull(r.delete_flag,'0') !='1' where ub.type='UserRole' and ub.key_id=tb.id limit 0,1) roleId, (select r.name from jsh_user_business ub left join jsh_role r on ub.value=concat("[",r.id,"]") and ifnull(r.delete_flag,'0') !='1' where ub.type='UserRole' and ub.key_id=tb.id limit 0,1) roleName from ( select user.id, user.username, user.login_name, user.position, user.email, user.phonenum, user.description, user.remark,user.isystem,org.id as orgaId,user.tenant_id,user.status,org.org_abr, rel.user_blng_orga_dspl_seq,rel.id as orgaUserRelId FROM jsh_user user left join jsh_orga_user_rel rel on user.id=rel.user_id and ifnull(rel.delete_flag,'0') !='1' left join jsh_organization org on rel.orga_id=org.id and ifnull(org.delete_flag,'0') !='1' where 1=1 and user.status !='1'
and user.username like #{bindUserName}
and user.login_name like #{bindLoginName}
order by rel.user_blng_orga_dspl_seq,user.id desc
limit #{offset},#{rows}
) tb

select count(user.id)
FROM jsh_user user
where 1=1
and user.status !='1'


    and user.username like #{bindUserName}



    and user.login_name like #{bindLoginName}

select user.id, user.username, user.login_name, user.position, user.email, user.phonenum, user.description, user.remark,user.isystem FROM jsh_user user where 1=1 and ifnull(user.status,'0') not in('1','2')
and user.userName = #{userName}
and user.login_name = #{loginName}
order by user.id desc

